mapper.select:
select *
from energy_account t


        t.is_deleted = 0


        and t.owner_type = #{ownerType}


        and t.owner_id in

            #{item}



        and t.owner_name like concat('%', #{ownerNameLike}, '%')


        and t.electric_account_type = #{electricAccountType}


        and warn_plan_id = #{warnPlanId}


order by t.id desc
