mapper.select:
select 
from energy_owner_space_rel
where owner_type = #{ownerType}
  and owner_id in

    #{ownerId}

select 
from energy_owner_space_rel
where owner_type = #{ownerType}
  and owner_id = #{ownerId}
  and space_id in

    #{spaceId}

mapper.delete:
delete
from energy_owner_space_rel
where owner_type = #{ownerType}
  and owner_id = #{ownerId}
  and space_id in

    #{spaceId}
