mapper.update:
update energy_account_balance
set balance = balance + #{amount}
where
balance_type = #{balanceType}
and balance_relation_id = #{balanceRelationId}
and account_id = #{accountId}
and is_deleted = 0

mapper.select:
select *
from energy_account_balance

    is_deleted = 0

        and balance_type = #{qo.balanceType}


        and balance_relation_id in

            #{item}



        and account_id in

            #{item}



order by account_id asc, id asc

mapper.update:
update energy_account_balance
set is_deleted = 1
where
balance_type = #{balanceType}
and balance_relation_id = #{balanceRelationId}
and is_deleted = 0
