mapper.select:
select *
from purchase_order
where order_sn = #{orderSn} limit 1;

mapper.update:
update purchase_order
set order_status = #{orderStatus}

    ,order_success_time = #{orderSuccessTime}

where order_sn = #{orderSn} limit 1;

mapper.select:
SELECT *
FROM purchase_order t

    t.is_deleted = 0

        AND t.order_status = #{orderStatus}


        AND t.order_create_time >= #{createStartTime}


        AND t.order_create_time < #{createEndTime}


        AND t.payment_channel = #{paymentChannel}


        AND t.user_id = #{userId}


ORDER BY t.order_create_time DESC
